let program,version,getForecast,saveApiToken;_bcb‍.x([["init",()=>init]]);_bcb‍.w("commander",[["default",["program"],function(v){program=v}]]);_bcb‍.w("../package.json",[["version",["version"],function(v){version=v}]]);_bcb‍.w("./main",[["default",["getForecast"],function(v){getForecast=v}]]);_bcb‍.w("./utils/apiToken",[["saveApiToken",["saveApiToken"],function(v){saveApiToken=v}]]);






       function init(args) {
    program
        .version(version, '-v, --version', 'Mostra a versão da ferramenta')
        .option('-t --token [token]', 'Advisor ClimaTempo API token')
        .arguments('<cityName...>')
        .description('Mostra o clima de uma cidade em tempo real')
        .action(async (cityName) => {
            if (program.token){
                await saveApiToken(program.token)
            }

            getForecast(cityName.join(' '))
        })
        .on('--help', () => {
            console.log()
            console.log('Exemplos: ')
            console.log('$ clima porto alegre')
            console.log('$ clima são paulo')
        })
        
    program.parse(args)
}
